--insert into patient values

CREATE OR REPLACE FUNCTION insert_person_records()
RETURNS VOID AS $$
DECLARE
    i INTEGER := 0;
BEGIN
    FOR i IN 5..30 LOOP
        VALUES (
            '00A' || CHR(65 + FLOOR(i / 26)::INTEGER % 26) || CHR(65 + i % 26),
            '00AA' || CHR(65 + FLOOR(RANDOM() * 10)::INTEGER)
        );
    END LOOP;
END;
$$ LANGUAGE plpgsql;


select insert_person_records();

